package cn.cenjp.platform.config;import org.springframework.security.access.AccessDeniedException;import org.springframework.security.web.WebAttributes;import org.springframework.security.web.access.AccessDeniedHandler;import org.springframework.stereotype.Service;import javax.servlet.ServletException;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;@Servicepublic class CustomAccessDeniedHandler implements AccessDeniedHandler {    @Override    public void handle(HttpServletRequest request,                       HttpServletResponse response,                       AccessDeniedException accessDeniedException) throws IOException, ServletException {        System.out.println("----------------无权限访问");            request.setAttribute(WebAttributes.ACCESS_DENIED_403, accessDeniedException);            response.sendError(HttpServletResponse.SC_FORBIDDEN,                    accessDeniedException.getMessage());    }}