package cn.cenjp.platform.dao;import cn.cenjp.platform.bean.Cart;import org.apache.ibatis.annotations.*;import java.util.List;@Mapperpublic interface CarDao {    @Select("select userId,goodName,spikeId,spikePrice,startTime,endTime,goodId" +            " from car " +            "where userId=#{userId}")    List<Cart> getCar(@Param("userId") Integer userId);    @Insert("insert into car(userId,goodName,spikeId,spikePrice,startTime,endTime,goodId) " +            "values(#{userId},#{goodName},#{spikeId},#{spikePrice},#{startTime},#{endTime},#{goodId})")    int joinCar(Cart cart);    @Delete("delete from car where userId=#{userId} and spikeId=#{spikeId}")    int deleteCar(@Param("userId") String userId,@Param("spikeId") String spikeId);}