package cn.cenjp.platform.dao;import cn.cenjp.platform.vo.Order;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import java.util.List;@Mapperpublic interface OderDao {    @Select("select goods.good_name as goodName,\n" +            "goods.good_originalPrice as goodOiginalPrice,\n" +            "spike_goods.spike_price as spikePrice,\n" +            "my_spike.myspike_date as spikeDate\n" +            "from goods,spike_goods,my_spike\n" +            "where goods.good_id=spike_goods.good_id\n" +            "and my_spike.spike_id=spike_goods.spike_id\n" +            "and my_spike.user_id=#{userId}\n")    List<Order> getOrder(Integer userId);}