package cn.cenjp.platform.dao;import cn.cenjp.platform.bean.Role;import org.apache.ibatis.annotations.*;import java.util.List;@Mapperpublic interface RoleDao {    @Select("select user_id ,role_name " +            "from role" +            " where user_id=#{userId};")    public List<Role> getRoleByUserId(@Param("userId") Integer userId);    @Insert("insert role(user_id,role_name)" +            " values(#{userId},#{roleName})")    public int insertRole(@Param("userId") String userId, @Param("roleName") String roleName);    @Delete("delete from role" +            " where user_id=#{userId} and role_name=#{roleName}")    public int deleteRole(@Param("userId") String userId,@Param("roleName") String roleName);}