package cn.cenjp.platform.service;import cn.cenjp.platform.bean.Cart;import cn.cenjp.platform.dao.CarDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CartService {    @Autowired    CarDao carDao;    public List<Cart> getCar(Integer userPhone) {        return carDao.getCar(userPhone) ;    }    public int joinCar(Cart cart){        return carDao.joinCar(cart);    }    public boolean deleteCar(String userId,String spikeId){        return carDao.deleteCar(userId,spikeId)>0;    }}