package cn.cenjp.platform.service;import cn.cenjp.platform.bean.Role;import cn.cenjp.platform.dao.RoleDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class RoleService {    @Autowired    RoleDao roleDao;    /**     * 获取用户所有的权限     * @param userId 用户id     * @return     */    public List<Role> getRole(Integer userId){        return roleDao.getRoleByUserId(userId);    }    /**     * 删除用户权限     * @param userId 用户id     * @param roleName 权限名     * @return 返回true或false 代表删除成功或失败     */    public boolean delteRole(String userId,String roleName){        return roleDao.deleteRole(userId,roleName)>0;    }    /**     * 添加用户权限     * @param userId 用户id     * @param roleName 用户名     * @return 返回true或false，代表增加成功或失败     */    public boolean insertRole(String userId,String roleName){        return roleDao.insertRole(userId,roleName)>0;    }}