package cn.cenjp.platform.service;import cn.cenjp.platform.Prefix.CountPrefix;import cn.cenjp.platform.Prefix.GoodVoKey;import cn.cenjp.platform.bean.Good;import cn.cenjp.platform.bean.SpikeGoods;import cn.cenjp.platform.dao.GoodDao;import cn.cenjp.platform.dao.GoodVoDao;import cn.cenjp.platform.dao.SpikeGoodsDao;import cn.cenjp.platform.redis.RedisService;import cn.cenjp.platform.utils.GetDate;import cn.cenjp.platform.vo.GoodVo;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Servicepublic class SendSpikeService {    Logger logger= LoggerFactory.getLogger(SendSpikeService.class);    @Autowired    GoodDao goodDao;    @Autowired    SpikeGoodsDao spikeGoodsDao;    @Autowired    GoodVoDao goodVoDao;    @Autowired    RedisService redisService;    @Transactional    public boolean sendSpike(Good good, SpikeGoods spikeGoods){        goodDao.insertGood(good);        int id = goodDao.getLast();        spikeGoods.setGood_id(id);        spikeGoodsDao.insertSpikeGoods(spikeGoods);        return true;    }    public List<GoodVo> getSend(String userId){      return goodVoDao.getSendList(userId);    }}