package cn.cenjp.platform.utils;import cn.cenjp.platform.Prefix.CountPrefix;import java.time.LocalDate;public class GetDate {    public static String getDate(){        LocalDate now = LocalDate.now();        return ""+now.getYear()+now.getMonthValue()+now.getDayOfMonth();    }    public static Integer getDay(){        LocalDate now = LocalDate.now();        return now.getDayOfMonth();    }    public static Integer getMonth(){        LocalDate now = LocalDate.now();        return now.getMonthValue();    }    public static Integer getYear(){        LocalDate now = LocalDate.now();        return now.getYear();    }    public static Integer getWeek(){        LocalDate now = LocalDate.now();        return now.getDayOfWeek().getValue();    }    public static void main(String[] args) {        System.out.println(new Integer(0));    }}