package cn.cenjp.platform.vo;import cn.cenjp.platform.bean.User;public class SpikeMessage {    private String goodId;    private User user;    private String spikeId;    public SpikeMessage() {    }    public SpikeMessage(String goodId, User user, String spikeId) {        this.goodId = goodId;        this.user = user;        this.spikeId = spikeId;    }    public String getGoodId() {        return goodId;    }    public void setGoodId(String goodId) {        this.goodId = goodId;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public String getSpikeId() {        return spikeId;    }    public void setSpikeId(String spikeId) {        this.spikeId = spikeId;    }    @Override    public String toString() {        return "SpikeMessage{" +                "goodId='" + goodId + '\'' +                ", user=" + user +                ", spikeId='" + spikeId + '\'' +                '}';    }}